import React, {useState} from 'react'
import NewPostInput from './NewPostInput'

const NewPost = () => {

    const [ myValue, setMyValue ] = useState({
        author: '',
        content: '',
        date: '',
        title: '',
    })

    const [testValue, setTestValue] = useState({
        author: ''
    })

    const onChange = (e) => {
        // let data = myValue
        // console.log(data)
        // data[e.target.name] = e.target.value
        // setMyValue(data)
        // console.log(myValue)
        const data = {...testValue}
        data.author = e.target.value

        setTestValue(data)

    }
    return(
<div>
        <form>
        <label>
            Author:
            <input 
            className="bg-gray-200 text-gray-700 border rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
            type =  'text'
            value = {testValue.author}
            name={"author"}
            onChange = {(e) => onChange(e)}
            />
        </label>

        </form>

        author: {testValue.author} <br />
        content: {myValue.content}<br />
        date: {myValue.date}<br />
        title: {myValue.title}<br />
        </div>
    )
}

export default NewPost
