import React, {useState} from 'react'
import NewPostInput from './NewPostInput'

const NewPost = () => {

    const [ myValue, setMyValue ] = useState({
        author: '',
        content: '',
        date: '',
        title: '',
    })



    const onChange = (e) => {

        const data = {...myValue}
        data[e.target.name] = e.target.value

        setMyValue(data)

    }
    return(
        <div>
            <form>
                <NewPostInput 
                value = {myValue.author}
                name = {'author'}
                onChange = {onChange}/>
                <NewPostInput 
                value = {myValue.title}
                name = {'title'}
                onChange = {onChange}/>
                <NewPostInput 
                value = {myValue.content}
                name = {'content'}
                onChange = {onChange}/>
                <NewPostInput 
                value = {myValue.date}
                name = {'date'}
                onChange = {onChange}/>

            </form>

        author: {myValue.author} <br />
        content: {myValue.content}<br />
        date: {myValue.date}<br />
        title: {myValue.title}<br />
        </div>
    )
}

export default NewPost
