import React, {useState} from 'react'
import NewPostInput from './NewPostInput'

const NewPost = () => {

    const [ myValue, setMyValue ] = useState({
        author: '',
        content: '',
        date: '',
        title: '',
    })

    const onChange = (e) => {
        console.log(e.target.name)

        const myData = myValue
        const name = e.target.name

        myData[name] = e.target.value
        console.log(myData)
        setMyValue(myData)

    }
    return(
<div>
        <form>
            <NewPostInput 
                value = {myValue["author"]} 
                name = {`author`} 
                onChange = {onChange}/>
        </form>

        author: {myValue.author}
        content: {myValue.content}
        date: {myValue.date}
        title: {myValue.title}
        </div>
    )
}

export default NewPost
