import connectToDb from './db'
const Post = require('../../mongoDocs/model')

export default async (req, res) => {
    await connectToDb();
    Post.find({}, (error, foundPosts) => {
        if(error) {
            response.status(400).json({error: error.message})
        }
        response.status(200).json(foundPosts)
    })
}
