const myData = [{
  author: "Steve",
  title: 'First Post',
  content: "Lots of stuff goes here",
  date: "2/1/2020",
  _id: 1

},{
  author: "Steve",
  title: 'Second Post',
  content: "Second Post Content",
  date: "2/2/2020",
  _id: 2
}]


// export default (req, res) => {
//   res.status(200).json(myData);
// };


export default (req, res) => {
  switch (req.method) {
    case 'GET':
      res.status(200).json(myData);
      break
    case 'POST':
      console.log(req)
      myData.push(req.body)
      console.log(myData)
      res.status(200).json(myData)
      break
    default:
      res.setHeader('Allow', ['GET', 'POST'])
      res.status(405).end() //Method Not Allowed
      break
  }
}
