import connectToDb from './db'
const Post = require('../../mongoDocs/model')

export default async (req, res) => {

    await connectToDb();

    const { email, password } = req.body;
    Post.find({}, (err, foundPosts) => {
        if(err) {
            res.status(400).json({error: err.message})
        }
        res.send()
    })

}
