import React, {useState} from 'react'

const NewPostInput = (props) => {


    return(
        <div className = "form-group">
            <label>
                {props.name}:


                <input 
                className="form-control"
                type =  'text'
                value = {props.value}
                name={props.name}
                onChange = {((e) => props.onChange(e))}

                />
            </label>
        </div>
    )
}

export default NewPostInput
