import React, { useState } from 'react'
import MyHead from './MyHead'
import Result from './Result'
import Header from './Header'
import Form from './Form'


const Home = () => {

    const [results, setResults] = useState([
        {
            author: "Steve",
            title: 'First Post',
            content: "Lots of stuff goes here",
            date: "2/1/2020"

        },{
            author: "Steve",
            title: 'Second Post',
            content: "Second Post Content",
            date: "2/2/2020"
        }
    ])

    const onChange = (e) => {
        const data = { ...results };

        let name = e.target.name;
        console.log(name)

        // so we are naming the inputs as Total Fat or Target Carbs, so this is splitting the name so we can do results[calories][total] - super cool way to use this on all inputs - especially if we have the objects combined liek this 


        let resultType = name.split(" ")[0].toLowerCase();
        let resultMacro = name.split(" ")[1].toLowerCase();
        console.log(resultType)
        console.log(resultMacro)

        data[resultMacro][resultType] = e.target.value;

        setResults(data);
      }

    return(
    <div>
        <MyHead pageName={'Home'} />
        <Header />
        <div className="flex mb-4 text-center">
            {results.map((post, index) => {
                return(
                <Result post = {post} key = {index}/>
                )
            })}
      </div>
      <div className="flex">
        <Form data={results} item="Total" onChange={onChange} />
        <Form data={results} item="Target" onChange={onChange} />
        <Form data={results} item="Variant" onChange={onChange} />
      </div>
      <div className="flex text-center">
        <div className="w-full m-4">
          <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
              Save
          </button>
        </div>
      </div>
    </div>
    )
}
export default Home
