import React, {useState} from 'react'
import NewPostInput from './NewPostInput'
import NewPostTextArea from './NewPostTextArea'

const NewPost = (props) => {

    const [ myValue, setMyValue ] = useState({
        author: '',
        content: '',
        date: '',
        title: '',
    })



    const onChange = (e) => {

        const data = {...myValue}
        data[e.target.name.toLowerCase()] = e.target.value

        setMyValue(data)

    }

    const onSubmit = (e) => {
        e.preventDefault()

        const newPosts = [...props.posts]
        newPosts.push(myValue)
        props.updateList(newPosts)
        // console.log(myValue)
    }
    return(
        <div>
            <form onSubmit = {(e) => onSubmit(e)}>
                <NewPostInput 
                value = {myValue.author}
                name = {'Author'}
                onChange = {onChange}
                />
                <NewPostInput 
                value = {myValue.title}
                name = {'Title'}
                onChange = {onChange}
                />
                <NewPostInput 
                value = {myValue.date}
                name = {'Date'}
                onChange = {onChange}
                />
                <NewPostTextArea 
                value = {myValue.content}
                name = {'Content'}
                onChange = {onChange}
                />
            <input type="submit" value="submit" className = "bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"/>
            </form>

        author: {myValue.author} <br />
        content: {myValue.content}<br />
        date: {myValue.date}<br />
        title: {myValue.title}<br />
        </div>
    )
}

export default NewPost
